$(function () {
	sliderInit()	
	replaceHash()
	lenisSetup()
	AOS.init({
		duration: 1500,
		disable: "mobile",
	});
})

function preLoader() {
	const tl = gsap.timeline({
		duration: 0.1
	});
	tl.to('.preLoader.black > img', {
			delay: 1,
			y: 50,
			autoAlpha: 0
		})
		.to('.preLoader.black', {
			yPercent: -100
		})
		.to('.preLoader.white', {
			yPercent: -100
		})
		.to('.preLoader', {
			css: {
				display: 'none'
			}
		})
		.from('.navbar-brand > img', {
			x: -50,
			autoAlpha: 0
		})
		.from(
			'.navbar-nav > li, .form-inline a', {
				autoAlpha: 0,
				x: 100,
				stagger: 0.05
			},
			'>-0.5'
		)
		.from('.slideOne', {
			autoAlpha: 0,
			yPercent: 100,
			stagger: 0.05
		})
		.from(
			'.bg-image', {
				autoAlpha: 0
			},
			'<-0.25'
		);
}

function lenisSetup() {
	const lenis = new Lenis({
		duration: 1.5,
		easing: (t) => Math.min(1, 1.001 - Math.pow(2, -10 * t)),
		smooth: true,
		mouseMultiplier: 1,
	});

	function raf(time) {
		lenis.raf(time);
		requestAnimationFrame(raf);
	}
	requestAnimationFrame(raf);
	document.querySelectorAll('a[href^="#"]').forEach((anchor) => {
		anchor.addEventListener("click", function (e) {
			e.preventDefault();
			lenis.scrollTo(this.getAttribute("href"));
		});
	});
	lenis.scrollTo("top");
}

function replaceHash() {
	document.querySelectorAll("a").forEach((a) => {
		let href = a.getAttribute("href");
		a.href = href ?
			href.startsWith("#") && href.endsWith("#") ?
			href.replace("#", "javascript:void(0)") :
			href :
			"javascript:void(0)";
	});
}

function sliderInit() {
	const swiperOptions = {
		loop: true,
		speed: 1000,
		parallax: true,
		autoplay: {
			delay: 3500,
			disableOnInteraction: false,
		},
		autoplay: false,
		grabCursor: false,
		pagination: {
			el: '.swiper-pagination',
			clickable: true
		},
		navigation: {
			nextEl: '.swiper-button-next',
			prevEl: '.swiper-button-prev'
		}
	};
	var bannerSlider = new Swiper('.bannerSlider', swiperOptions);
	var pageSections = document.querySelectorAll('.bannerImg');
	pageSections.forEach(function (pageSection) {
		var backgroundUrl = pageSection.getAttribute('data-background');
		if (backgroundUrl) {
			pageSection.style.backgroundImage = 'url(' + backgroundUrl + ')';
		}
	});

	var logoSlider = new Swiper('.logoSlider', {
		loop: true,
		autoplay: true,
		slidesPerView: 1,
		spaceBetween: 10,
		centeredSlides: true,
		roundLengths: true,
		breakpoints: {
			1199: {
				slidesPerView: 5,
				spaceBetween: 10
			},
			768: {
				slidesPerView: 4,
				spaceBetween: 10
			},
			576: {
				slidesPerView: 3,
				spaceBetween: 10
			},
			375: {
				slidesPerView: 2,
				spaceBetween: 10
			}
		},
	});

	var industrySlider = new Swiper('.industrySlider', {
		loop: true,
		slidesPerView: 4,
		spaceBetween: 20,
		breakpoints: {
			1024: {
				slidesPerView: 4,
				spaceBetween: 20
			},
			768: {
				slidesPerView: 1,
				spaceBetween: 20
			},
			640: {
				slidesPerView: 1,
				spaceBetween: 10
			},
			576: {
				slidesPerView: 1,
				spaceBetween: 10
			},
			375: {
				slidesPerView: 1,
				spaceBetween: 10
			}
		},
		navigation: {
			nextEl: '.swiper-button-next',
			prevEl: '.swiper-button-prev'
		},
		scrollbar: {
			el: '.swiper-scrollbar'
		}
	});
}